import React from 'react';
import { connect } from 'react-redux';
import Proptypes from 'prop-types';
import { saveExpensesAction } from '../../actions';

class Form extends React.Component {
  /* handleClick = (event) => {
    event.preventDefault();
    // const { category, currencies, currency } = this.props;
    this.setState({
      category: this.category,
      currencies: this.currencies,
      currency: this.currency,
    });
  } */
  // handleChange =

  render() {
    const {
      category,
      currencies,
      currency,
      description,
      value,
      method,
    } = this.props;
    return (
      <form>
        <label htmlFor="valueInput">
          Valor:
          <input
            data-testid="value-input"
            name="value"
            onChange={ (event) => this.setState({ value: event.target.value })}
            type="number"
            value={ value }
            id="value"
          />
        </label>

        <label htmlFor="description">
          Descrição:
          <input
            data-testid="description-input"
            name="description"
            // onChange={ handleChange }
            type="text"
            value={ description }
            id="description"
          />
        </label>

        <label htmlFor="coin">
          Moeda:
          <select
            data-testid="currency-input"
            value={ currency }
            // onChange={ handleChange }
            name="coin"
            id="coin"
          >
            {currencies.map((element) => (
              <option key={ element }>{element}</option>
            ))}
          </select>
        </label>

        <label htmlFor="method">
          Método de pagamento:
          <select
            data-testid="method-input"
            value={ method }
            // onChange={ this.handleChange }
            name="method"
            id="method"
          >
            <option>Dinheiro</option>
            <option>Cartão de crédito</option>
            <option>Cartão de débito</option>
          </select>
        </label>

        <label htmlFor="category">
          Categoria
          <select
            data-testid="tag-input"
            name="category"
            // onChange={ handleChange }
            value={ category }
          >
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>

        <button
          type="button"
          name="addExpense"
          onClick={ () => this.props.saveExpensesAction() }
        >
          Adicionar despesa
        </button>

      </form>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  saveExpensesAction: (information) => dispatch(saveExpensesAction(information)),
});

Form.propTypes = {
  currencies: Proptypes.arrayOf(Proptypes.any),

}.isRequired;

export default connect(null, mapDispatchToProps)(Form);
