import React from 'react';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      isValid: false,
    };
  }

  allowLoginBtn = () => {
    const { password } = this.state;
    const magicNumber = 6;
    if (password.length >= magicNumber) this.setState({ isValid: true });
  }

  handleChange = (event) => {
    const { value, name } = event.target;
    this.setState(
      {
        [name]: value,
      },
      this.allowLoginBtn,
    );
  }

  render() {
    const { email, password, isValid } = this.state;
    return (
      <main>
        <header>TrybeWallet</header>
        <div>
          <form>
            <input
              data-testid="email-input"
              placeholder="Digite seu email"
              type="email"
              name="email"
              value={ email }
              onChange={ this.handleChange }
            />
            <input
              placeholder="Digite sua senha"
              name="password"
              type="password"
              value={ password }
              data-testid="password-input"
            // onChange = { this.handleChange }
            />
            <button
              type="button"
              name="loginBtn"
              disabled={ isValid }
              // onClick={ (event) => this.handleClick(event, email) }
            >
              Entrar
            </button>
          </form>
        </div>
      </main>

    );
  }
}

export default Login;
